package com.example.crudspringboot.Services.UserServiceIMPL;import com.example.crudspringboot.Entity.Users;import com.example.crudspringboot.Repository.IUsersRepository;import com.example.crudspringboot.Services.usersService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;//Users Implementation@Servicepublic class UsersImplementation implements usersService {    @Autowired    private IUsersRepository repository;    @Override    public List<Users> getUser() {        return (List<Users>) repository.findAll();    }    @Override    public Users createdUser(Users Users) {        Users.setEmail(Users.getEmail());        return repository.save(Users);    }    @Override    public Users updateUser(Long id, Users user) {        user.setId(id);        return repository.save(user);    }    @Override    public Users searchUser(Long id) {        return repository.findById(id).get();    }    @Override    public void deleteUser(Long id) {        repository.deleteById(id);    }}